$("#btn-update-user").on("click",function(){var a=$("#form-user").serialize();$(".form-msg").remove(),$(this).closest(".page-box-footer").append('<div class="ajax-info"><span id="ico-load" class="ion-load-c spin"></span> </div>'),$.ajax({url:user.ajaxurl,type:"GET",data:{action:"update_user",user_dados:a},success:function(a){$(".ajax-info").fadeOut(300);var s=JSON.parse(a);$('input[name="'+s.name+'"]').addClass("field-error"),$("#form-user").prepend('<div class="form-msg '+s.class+' ">'+s.mensagem+"</div>")}})});